const START_SPEED = 400;

// When the player is added, set its velocity, camera offset, and starting position

(k, player) => {
  player.cameraOffset = k.vec2(0, 0);
  player.velocity = k.vec2(0, -START_SPEED);
  player.mouseDriven = false;
  player.pos.x = window.innerWidth * 0.5;
  player.startPos = k.vec2(player.pos.x, player.pos.y);
}

// Move the player character by its velocity every frame

(k, player) => {
  player.move(player.velocity);

  const deltaX = player.pos.x - player.startPos.x;
  const deltaY = player.pos.y - player.startPos.y;
  const dist = Math.sqrt((deltaX * deltaX) + (deltaY * deltaY));

  k.ext.data.score = Math.floor(dist);
}

// Adjust the starfield such that its width matches the window's width

(k, starfield) => {
  starfield.scale.x = window.innerWidth / 512.0;
  starfield.scale.y = starfield.scale.x
  starfield.pos.x = window.innerWidth * 0.5;
  if (starfield.is('starfield-two')) {
    starfield.pos.y -= (starfield.height * starfield.scale.y);
  }
}

// Move the player left, but not outside of the screen boundary

(k) => {
  k.every('player', (player) => {
    player.move(k.vec2(-player.velocity.len(), 0));
    k.every('camera', (camera) => {
      player.pos.x = Math.max(player.pos.x, camera.pos.x - (window.innerWidth * 0.5));
    });
  });
}

// Move the player right, but not outside of the screen boundary

(k) => {
  k.every('player', (player) => {
    player.move(k.vec2(player.velocity.len(), 0));
    k.every('camera', (camera) => {
      player.pos.x = Math.min(player.pos.x, camera.pos.x + (window.innerWidth * 0.5));
    });
  });
}

// Move the player up, but not outside of the screen boundary

(k) => {
  k.every('player', (player) => {
    player.move(k.vec2(0, -player.velocity.len()));
    k.every('camera', (camera) => {
      player.pos.y = Math.max(player.pos.y, camera.pos.y - (window.innerHeight * 0.5));
    });
  });
}

// Every frame, move the camera the same way as the player

(k, camera) => {
  k.every('player', (player) => {
    camera.move(player.velocity);
  });
  k.every('scoreboard', (scoreboard) => {
    scoreboard.pos.x = camera.pos.x - (window.innerWidth * 0.5) + 60;
    scoreboard.pos.y = camera.pos.y - (window.innerHeight * 0.5) + 60;
  });
  k.camPos(camera.pos);
}

// When the camera is added, set its position to the center of the window

(k, camera) => {
  camera.pos.x = window.innerWidth * 0.5;
  camera.pos.y = window.innerHeight * 0.5;
}

// Move the player down, but not outside of the screen boundary

(k) => {
  k.every('player', (player) => {
    player.move(k.vec2(0, player.velocity.len()));
    k.every('camera', (camera) => {
      player.pos.y = Math.min(player.pos.y, camera.pos.y + (window.innerHeight * 0.5));
    });
  });
}

// Move the bullet forward each frame, and destroy it if it's far from the player

(k, bullet) => {
  const player = k.get('player')[0];
  if (!player || bullet.is('demo-bullet')) {
    return;
  }
  bullet.move(bullet.velocity);
  const deltaX = player.pos.x - bullet.pos.x;
  const deltaY = player.pos.y - bullet.pos.y;
  const dist = Math.sqrt((deltaX * deltaX) + (deltaY * deltaY));
  if (dist > 1000) {
    k.destroy(bullet);
  }
}

// Spawn bullets

const BULLET_VELOCITY = 2500;

window.addBullets = (k, scale, pos, spread, yOffset) => {
  const bulletLeft = k.add([
    'bullet',
    k.pos(pos.x - spread, pos.y + yOffset),
    k.scale(scale.x, scale.y),
    k.sprite('bullet.png'),
    k.origin('center'),
  ]);
  bulletLeft.velocity = k.vec2(0, -BULLET_VELOCITY);

  const bulletRight = k.add([
    'bullet',
    k.pos(pos.x + spread, pos.y + yOffset),
    k.scale(scale.x, scale.y),
    k.sprite('bullet.png'),
    k.origin('center'),
  ]);
  bulletRight.velocity = k.vec2(0, -BULLET_VELOCITY);
};

(k) => {
  const player = k.get('player')[0];
  const demoBullet = k.get('demo-bullet')[0];
  if (player && demoBullet) {
    addBullets(k, demoBullet.scale, player.pos, player.width * 0.35, 6);  
  }
}

// Adjust the starfields positions as needed

(k) => {
  const sf1 = k.get('starfield-one')[0];
  const sf2 = k.get('starfield-two')[0];
  const player = k.get('player')[0];
  if (!sf1 || !sf2 || !player) {
    return;
  }

  const height1 = sf1.height * sf1.scale.y;
  const height2 = sf2.height * sf2.scale.y;

  if (player.pos.y < (sf1.pos.y - (height1 * 0.5)) && player.pos.y > (sf1.pos.y - height1)) {
    sf2.pos.y = sf1.pos.y - height1;
  } else if (player.pos.y < (sf2.pos.y - (height2 * 0.5)) && player.pos.y > (sf2.pos.y - height2)) {
    sf1.pos.y = sf2.pos.y - height2;
  }
}

// Mouse down turns it into mouse-driven mode

(k) => {
  k.every('player', (player) => {
    player.mouseDriven = true;
  });
}

// If the player is mouse-driven, drive it by the mouse

(k) => {
  const player = k.get('player')[0];
  const camera = k.get('camera')[0];
  if (!player || !player.mouseDriven || !camera) {
    return;
  }

  const mousePos = k.mousePos();
  player.pos.x = mousePos.x;
  player.pos.y = mousePos.y + camera.pos.y - (window.innerHeight * 0.5);

  const demoBullet = k.get('demo-bullet')[0];
  if (demoBullet) {
    addBullets(k, demoBullet.scale, player.pos, player.width * 0.35, 6);  
  }
}

// Mouse release turns it into keyboard-driven mode

(k) => {
  k.every('player', (player) => {
    player.mouseDriven = false;
  });
}

(k, scoreboard) => {
  scoreboard.text = '' + k.ext.data.score;
}

(k) => {
  const player = k.get('player')[0];

  if (Math.random() < 0.025) {
    k.add([
      k.pos(Math.random() * window.innerWidth, player.pos.y - ((1 + Math.random()) * window.innerHeight)),
      k.sprite('enemy-spaceship-1.png'),
      k.scale(0.25, 0.25),
      k.rotate(3.141592653589793238462643383279),
      k.origin('bot'),
      'enemy1',
    ]);
  }

  const toDestroy = [];
  k.every('enemy1', (enemy1) => {
    if (enemy1.pos.y - 300 > player.pos.y) {
      toDestroy.push(enemy1);
    }
  });
  toDestroy.forEach(k.destroy);
}

(k, bullet, enemy1) => {
  k.destroy(enemy1);
}

(k, enemy1, player) => {
  k.ext.scores.submit(k.ext.data.score);
  k.go('score');
}

const ROW_OFFSET = 35;

(k, finalscore) => {
  finalscore.pos.x = window.innerWidth * 0.5;
  finalscore.pos.y = window.innerHeight * 0.7;

  let y = finalscore.pos.y - (ROW_OFFSET * 2);
  for (let i = 5; i > 0; --i) {
    const obj = k.get(`score${i}`)[0];
    if (obj) {
      obj.pos.x = finalscore.pos.x;
      obj.pos.y = y;
      y -= ROW_OFFSET;
    }
  }
}

(k) => {
  k.go('main');
}

