}

const TOP_SCORES = [1, 2, 3, 4, 5];
const DATE_FMT = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };

(k, finalscore) => {
  const setScoreText = (num, text) => {
    const obj = k.get('score' + num)[0];
    if (obj) {
      obj.text = text;
    }
  };

  finalscore.text = 'YOUR SCORE: ' + k.ext.data.score;
  TOP_SCORES.forEach(i => setScoreText(i, ''));

  k.ext.scores.load().then((scores) => {
    scores.sort((a, b) => (a.score || 0) - (b.score || 0));

    const d = new Date();

    TOP_SCORES.forEach(i => {
      const score = scores[i - 1];
      if (score) {
        d.setTime(score.ts);
        const dFmt = d.toLocaleDateString('en-US', DATE_FMT);
        setScoreText(i, '' + i + ': ' + score.score + ' - ' + dFmt);
      } else {
        setScoreText(i, '');
      }
    });
  });
}

